 if fromSignature'="" {
	set line=""
	for {
		set data=""
		set line=$Order(^ompare("DataSrc",fromSignature,line),+1,data)
		quit:line=""
		write !,"  value+='",$ZCONVERT(data,"O","JS"),"\n';"
	} 
 } else {
	write !,"  value+='Source Content not found';"
 }

