		set line=$Order(^ompare("DataSrc",toSignature,line),+1,data)
		quit:line=""
		write !,"  orig2+='",$ZCONVERT(data,"O","JS"),"\n';"
	} 
 } else {
	write !,"  orig2+='Source Content not found';"
