Method.Implementation:
// set up summary paramaters set:..SummaryShowSameClasses summary(..#RPTTypeClasses,..#RPTSame)=1 set:..SummaryShowDifferentClasses summary(..#RPTTypeClasses,..#RPTDifferent)=1 set:..SummaryShowMissingClasses summary(..#RPTTypeClasses,..#RPTMissing)=1 set:..SummaryShowExtraClasses summary(..#RPTTypeClasses,..#RPTExtra)=1 set:..SummaryIncludeMappedClasses config(..#RPTTypeClasses,..#RPTMapped)=1 set:..SummaryIncludeUncontrolledClasses config(..#RPTTypeClasses,..#RPTSourceControlled)=1
// set up detail parameters set:..DetailShowSameSummary detail(..#RPTSubTypeSummary,..#RPTSame)=1 set:..DetailShowDifferentSummary detail(..#RPTSubTypeSummary,..#RPTDifferent)=1
set:..DetailShowSameMethodsSig detail(..#RPTSubTypeMethodSignature,..#RPTSame)=1 set:..DetailShowDifferentMethodsSig detail(..#RPTSubTypeMethodSignature,..#RPTDifferent)=1 set:..DetailShowMissingMethodsSig detail(..#RPTSubTypeMethodSignature,..#RPTMissing)=1 set:..DetailShowExtraMethodsSig detail(..#RPTSubTypeMethodSignature,..#RPTExtra)=1
set:..DetailShowSameMethods detail(..#RPTSubTypeMethod,..#RPTSame)=1 set:..DetailShowDifferentMethods detail(..#RPTSubTypeMethod,..#RPTDifferent)=1 set:..DetailShowMissingMethods detail(..#RPTSubTypeMethod,..#RPTMissing)=1 set:..DetailShowExtraMethods detail(..#RPTSubTypeMethod,..#RPTExtra)=1
set:..DetailShowSameParameters detail(..#RPTSubTypeParameter,..#RPTSame)=1 set:..DetailShowDifferentParameters detail(..#RPTSubTypeParameter,..#RPTDifferent)=1 set:..DetailShowMissingParameters detail(..#RPTSubTypeParameter,..#RPTMissing)=1 set:..DetailShowExtraParameters detail(..#RPTSubTypeParameter,..#RPTExtra)=1
set:..DetailShowSameProperties detail(..#RPTSubTypeProperty,..#RPTSame)=1 set:..DetailShowDifferentProperties detail(..#RPTSubTypeProperty,..#RPTDifferent)=1 set:..DetailShowMissingProperties detail(..#RPTSubTypeProperty,..#RPTMissing)=1 set:..DetailShowExtraProperties detail(..#RPTSubTypeProperty,..#RPTExtra)=1
set:..DetailShowSameXData detail(..#RPTSubTypeXData,..#RPTSame)=1 set:..DetailShowDifferentXData detail(..#RPTSubTypeXData,..#RPTDifferent)=1 set:..DetailShowMissingXData detail(..#RPTSubTypeXData,..#RPTMissing)=1 set:..DetailShowExtraXData detail(..#RPTSubTypeXData,..#RPTExtra)=1
set:..DetailShowSameStorage detail(..#RPTSubTypeStorage,..#RPTSame)=1 set:..DetailShowDifferentStorage detail(..#RPTSubTypeStorage,..#RPTDifferent)=1 set:..DetailShowMissingStorage detail(..#RPTSubTypeStorage,..#RPTMissing)=1 set:..DetailShowExtraStorage detail(..#RPTSubTypeStorage,..#RPTExtra)=1
set key="" for { set pattern=..PatternClassesExclude.GetNext(.key) quit:key="" continue:pattern="" set exclude(..#RPTTypeClasses,pattern)="" } for { set pattern=..PatternClassesInclude.GetNext(.key) quit:key="" continue:pattern="" set include(..#RPTTypeClasses,pattern)="" } for { set pattern=..PatternMethodsExclude.GetNext(.key) quit:key="" continue:pattern="" set exclude(..#RPTSubTypeMethod,pattern)="" set exclude(..#RPTSubTypeMethodSignature,pattern)="" } for { set pattern=..PatternMethodsInclude.GetNext(.key) quit:key="" continue:pattern="" set include(..#RPTSubTypeMethod,pattern)="" set include(..#RPTSubTypeMethodSignature,pattern)="" }

quit:..IsEmptyReport()
set elapstart=$P($ZTS,",",2)

W !,"<span class=""SectionTitle"">Section: Classes</span>"
W "<a name=""show"_..#PageAnchor_"""></a>"
W:'isFirstDisplayed "&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;[<a href=""#showTop"">Back to Top</a>]"
W !,"<table border=1>"
set sequence=0
W !," <tr>"
W !,"  <th>Item</th>"
W !,"  <th>ClassName</th>"
W !,"  <th>",definition.FromEnvironment,"<br/>",..GetDateTimeOfData(definition.FromEnvironment),"</th>"
	set env=""
for {
	if definition.HistoricView {
		set env=$Order(^||ReportData("Meta","To",env),-1,data)
	} else {
		set env=$Order(^||ReportData("Meta","To",env),+1,data)
	}
	quit:env=""
	set date=$P(data,"^")
	set ns=$P(data,"^",2)
	W !,"<th>",$ZCONVERT(env,"O","HTML"),"<br/>",$ZCONVERT(ns,"O","HTML"),"<br/>",$ZD(date,3),"<br/>"
	//W "<a target=""_blank"" href=""ompare.Report.GenerateReportAsIRISProject.cls?report="
	//W $ZCONVERT(definition.ReportName,"O","URL")
	//W "&targetEnv="
	//W $ZCONVERT(env,"O","URL")
	//W """>[Project]</a>"
	W "</th>"
}

set classname=""
for {
	set classname=$O(^||ReportData(..#RPTTypeClasses,classname))
	quit:classname=""
	// skipping empty analysis
	// For example there is a difference in Properties BUT Property differences has not been selected in the report
	// Validate for when no difference detail has been requested?
	// Unless an artifact has been included in the report subType
	// configuration, ignore SubTypes that are not of interest.
	if '..SummaryShowMissingClasses,'..SummaryShowExtraClasses,'..SummaryShowSameClasses {
		continue:'$Data(^||ReportData(..#RPTTypeClasses,classname,"d"))
	}
	W !," <tr>"
	W !,"   <td>",$Increment(sequence),"</td>"
	W !,"  <td>",$ZCONVERT(classname,"O","HTML"),"</td>"

	set toNs=$O(^||ReportData(..#RPTTypeClasses,classname,"s",definition.FromEnvironment,""),+1,result)

	if toNs="" {
		W "<td>"
		set depRSrow=0
		if ##class(%ClassDefinition).%ExistsId(classname) {
			set oClassDef=##class(%ClassDefinition).%OpenId(classname,0)
			if $IsObject(oClassDef),'oClassDef.Abstract {
				set depRS=##class(%ResultSet).%New()
				set depRS.ClassName="%Dictionary.ClassDefinitionQuery"
				set depRS.QueryName="SubclassOf"
				do depRS.Execute(classname)

				for {
					quit:'depRS.Next(.sc)
					set depRSrow=depRSrow+1
					W:depRSrow=1 ..ASHTML("Dependencies:")_"<br/>"
					W ..ASHTML(depRS.Data("Name"))_"<br/>"
				}
				do depRS.Close()
			}
		}
		W:depRSrow=0 " - "
		W "</td>"
	} else {
		W "<td>",$Case(result,..#RPTSame:"Same",..#RPTDifferent:"Different",..#RPTMissing:"Missing",..#RPTExtra:"Extra",:" - "),"</td>"
	}
	set env=""
	for {
		// TODO - Limitation of Date Ordering for Historic
		if (definition.HistoricView) {
			set env=$Order(^||ReportData("Meta","To",env),-1,date)
		} else {
			set env=$Order(^||ReportData("Meta","To",env),+1,date)
		}
		quit:env=""
			set toNs=$O(^||ReportData(..#RPTTypeClasses,classname,"s",env,""),+1,result)
		if toNs="" {
			W "<td> - </td>"
		} else {
			// From date is always current
			set lnk("sfEnv")=definition.FromEnvironment
			set lnk("sfNS")=$Select($D(lnk("sfEnv")):$O(definition.EnvironmentsMD(lnk("sfEnv"),"")),1:"")
			set lnk("sfT")="C"
			set lnk("sfTN")=classname
			set lnk("sfST")=""  // eg: "M"
			set lnk("sfSTN")=""  // eg: OnProcessInput
			if definition.HistoricView {
				set lnk("stD")=+date
				set lnk("stEnv")=$ZSTRIP($P(env,"("),">W")  // Remove History Discriptor
			} else {
				kill lnk("stD")
				set lnk("stEnv")=env
			}
			set lnk("stNS")=$Select($D(lnk("stEnv")):$O(definition.EnvironmentsMD(lnk("stEnv"),"")),1:"")
			set lnk("stT")="C"
			set lnk("stTN")=classname
			set lnk("stST")=""  // eg: "M"
			set lnk("stSTN")=""  // eg: OnProcessInput

			W "<td style=""vertical-align:top"""
			if result=..#RPTDifferent {
				if definition.DisplayDifferenceDetail {
					W "><div>",..ReportDetailAltTextWithLink(..#RPTTypeClasses,classname,env,,.lnk,result,definition.DisplayComments),"</div>"
				} else {
					W " title=""",$ZCONVERT(..ReportDetailAltText(..#RPTTypeClasses,classname,env),"O","HTML"),""">Different"
				}
			} else {
				// TODO - If comments are active then have some link / mechanism to add change comment
				W ">",$Case(result,..#RPTSame:"Same Class",..#RPTMissing:"Missing Class",..#RPTExtra:"Extra Class",:" - ")
			}
			W "</td>"
		}
	}
	W !," </tr>"
}	
W !,"</table>"
set elapend=$P($ZTS,",",2)
W !,"(Time taken:",$FN(elapend-elapstart,"",2),") seconds"
W !,"<hr/>"
W !,"<br/>"

if +$G(out(..#RPTSubTypeSummary))>0 {
	set return=return_$S($L(return):eol,1:"")_"Summary: "
	for i=1:1:out(..#RPTSubTypeSummary) {
		set return=return_eol_" *"_$G(out(..#RPTSubTypeSummary,i,"name"))_" ("_..RTPEnumToText($G(out(..#RPTSubTypeSummary,i,"result")))_")"
	}
}
